export const InputFText = React.memo(({ styleClass, formKey, formRef, uiRefresh, label, placeholder, required = "" }) => {
    const isNotValid = () => required && formRef.current.isSubmit && !formRef.current[formKey];
    const inValidBorder = () => isNotValid() ? ' border-red-500' : '';
    const [, refresh] = useState(-1);
    const setFormVal = (e) => {
        formRef.current[formKey] = e.currentTarget.value;
        refresh(Date.now());
    }
    return (
        <div className={`${styleClass}${isNotValid() ? ' mark-err' : ''}`}>
            <label className={`text-gray-400 mb-1${required ? ' required' : ''}`}>{label}</label>
            <input type="text" required={!!required} className={`input-field rounded${inValidBorder()}`} placeholder={placeholder} value={formRef.current[formKey]} onChange={e => setFormVal(e)} />
            {isNotValid() && <div className='flex justify-start items-center text-red-500 text-xs mt-1'>{required}</div>}
        </div>
    );
});

